import { useState } from "react";
import $ from "jquery";
import "./App.css";
import ReactDOM from 'react-dom/client';

function App() {
    const [name, setName] = useState("");
    const [result, setResult] = useState("");

    const HomeAPI = 'http://localhost:8000/api/index.php';
    const PeopleAPI = 'http://localhost:8000/api/list-people.php';
    const PlanetsAPI = 'http://localhost:8000/api/list-planets.php';
    const StarshipsAPI = 'http://localhost:8000/api/list-starships.php';

    const homePageFetch = () => {
      fetch(HomeAPI)
      .then(response => response.text())
      .then(text => {
          var homePage = document.getElementsByClassName('homepage');

          for (var i = 0; i < homePage.length; i ++) {
              homePage[i].style.display = 'block';
          }

          var gallery = document.getElementsByClassName('gallery');

          for (var i = 0; i < gallery.length; i ++) {
              gallery[i].style.display = 'none';
          }
      });
    }

    const peopleFetch = () => {
      fetch(PeopleAPI)
      .then(response => response.text())
      .then(text => {
          var homePage = document.getElementsByClassName('homepage');

          for (var i = 0; i < homePage.length; i ++) {
              homePage[i].style.display = 'none';
          }
          setResult(text)
          console.log(text)
           // do something else with the text
      });
    }

    const planetFetch = () => {
      fetch(PlanetsAPI)
      .then(response => response.text())
      .then(text => {
          setResult(text)
          console.log(text)
      });
    }

    const starshipsFetch = () => {
      fetch(StarshipsAPI)
      .then(response => response.text())
      .then(text => {
          setResult(text)
          console.log(text)
      });
    }

    return (
        <div className="App">
          <div className="topnav">
            <a className="non-clickable" href="#" onClick={homePageFetch}>The Star Wars API</a>
            <a className="links people" href="#people" onClick={peopleFetch}>People</a>
            <a className="links planets" href="#planets" onClick={planetFetch}>Planets</a>
            <a className="links starships" href="#starships" onClick={starshipsFetch}>Starships</a>
          </div>
          <br></br>
          <div className="homepage">
            <div className="star-header first-header">Base API: 
              <a className="link-style" href="https://swapi.dev/documentation">The Star Wars API</a>
            </div>
            <div className="star-header">Built by: 
              <a className="link-style" href="https://github.com/athiraindrakumar396">Athira Indrakumar</a>
            </div>
            <div className="star-header">Code available in: 
              <a className="link-style" href="https://github.com/athiraindrakumar396">Github</a>
            </div>
          </div>
          <div className='gallery'>
            <ul>
              { result.map((response, i) => (
 <div key={i}>
  <img className='img-prew' src={response.images} />
 </div>
)) }
            </ul>
          </div>
        </div>
    );
}

export default App;
