import { useState } from "react";
import React from 'react';
import $ from "jquery";
import "./App.css";
import axios from 'axios';

function App() {

    // all initialisations
    let peoplePageVal = 1;
    let planetPageVal = 1;
    let starshipPageVal = 1;
    let listPeople = [];
    const [result, setResult] = useState("");

    // all API paths defined below
    const HomeAPI = 'http://localhost:8000/api/index.php';
    const PeopleAPI = 'http://localhost:8000/api/list-people.php';
    const PlanetsAPI = 'http://localhost:8000/api/list-planets.php';
    const StarshipsAPI = 'http://localhost:8000/api/list-starships.php';
    const PersonAPI = 'http://localhost:8000/api/search-person.php';
    const ViewPeopleAPI = 'http://localhost:8000/api/view-people.php';
    const ViewPlanetsAPI = 'http://localhost:8000/api/view-planets.php';
    const ViewStarshipsAPI = 'http://localhost:8000/api/view-starships.php';

    // hide homepage when other pages are rendered
    const hideHomePage = () => {
      $('.homepage').hide()
    }

    // show homepage when needed
    const showHomePage = () => {
      $('.homepage').show()
    }

    // show people, planets or starships when needed
    const showPeople = () => {
      $('.gallery').show()
    }

    // hide people, planets or starships when other pages are rendered
    const hidePeople = () => {
      $('.gallery').hide()
    }

    // hide people search when on other pages
    const hideFilterBox = () => {
      $('.filter-text').hide()
    }

    // show people search when on people list page
    const showFilterBox = () => {
      $('.filter-text').show()
    }

    // display homepage
    const homePageFetch = () => {
      fetch(HomeAPI)
      .then(response => response.text())
      .then(text => {
          showHomePage()
          hidePeople();
          hideFilterBox()
      });
    }

    // display people listing page
    const peopleFetch = () => {
      axios
        .post(PeopleAPI, {
            pageValue: peoplePageVal
        })
      .then(function(body) {
          hideHomePage()
          showPeople()
          showFilterBox()
          if (body['data'].length !== 0) {
            listPeople.push((body['data'].map(
                (element, i) => {
                      return (
                      <li className={'listPeople'}  style={{marginTop: '30px', listStyleType: 'none'}} key={element.name}>
                         <a href="#view" style={{color:'white'}}> {element.name}</a>
                      </li>
                    )
                }
            )))
            if (listPeople.length !== 0) {
              setResult(Array.prototype.concat.apply([], listPeople))
            }

            if (body['data'][0] && body['data'][0].hasOwnProperty('next')) {
              peoplePageVal = peoplePageVal + 1
              peopleFetch()
            }
          }
      });
    }

    // display planets listing page
    const planetFetch = () => {
      axios
        .post(PlanetsAPI, {
            planetPageValue: planetPageVal
        })
      .then(function(body) {
          hideHomePage()
          showPeople()
          if (body['data'].length !== 0) {
            listPeople.push((body['data'].map(
                (element, i) => {
                      return (
                      <li className={'listPeople'}  style={{marginTop: '30px', listStyleType: 'none'}} key={element.name}>
                         <a href="#view" style={{color:'white'}}> {element.name}</a>
                      </li>
                    )
                }
            )))
            if (listPeople.length !== 0) {
              setResult(Array.prototype.concat.apply([], listPeople))
            }

            if (body['data'][0] && body['data'][0].hasOwnProperty('next')) {
              planetPageVal = planetPageVal + 1
              planetFetch()
            }
          }
      });
    }

    // display starships listing page
    const starshipsFetch = () => {
      axios
        .post(StarshipsAPI, {
            starshipPageValue: starshipPageVal
        })
      .then(function(body) {
          hideHomePage()
          showPeople()
          if (body['data'].length !== 0) {
            listPeople.push((body['data'].map(
                (element, i) => {
                      return (
                      <li className={'listPeople'}  style={{marginTop: '30px', listStyleType: 'none'}} key={element.name}>
                         <a href="#view" onClick={viewStarship(element.id)} style={{color:'white'}}> {element.name}</a>
                      </li>
                    )
                }
            )))
            if (listPeople.length !== 0) {
              setResult(Array.prototype.concat.apply([], listPeople))
            }

            if (body['data'][0] && body['data'][0].hasOwnProperty('next')) {
              starshipPageVal = starshipPageVal + 1
              starshipsFetch()
            }
          }
      });
    }

    // search for people in people listing page
    const listenToKey = (e) => {
        axios
        .post(PersonAPI, {
            item: e.target.value
        })
        .then(function(body) {
          const listItems = body['data'].map(
              (element, i) => {
                  return (
                    <li className={'listPeople'}  style={{marginTop: '30px', listStyleType: 'none'}} key={i}>
                       <a href="#view" style={{color:'white'}}> {element.name}</a>
                    </li>
                  )
              }
          )

          setResult(listItems)
          if (e.target.value === '') {
            peoplePageVal = 1
            peopleFetch()
          }
        })
        .catch(function(error) {
          console.log(error);
        });
    }

    // display starships listing page
    const viewStarship = (id) => {
      axios
        .post(ViewStarshipsAPI, {
            starshipId: id
        })
      .then(function(body) {
          hideHomePage()
          hidePeople()
          if (body['data'].length !== 0) {
            listPeople.push((body['data'].map(
                (element, i) => {
                      return (
                      <li className={'listPeople'}  style={{marginTop: '30px', listStyleType: 'none'}} key={element.name}>
                         <a href="#view" onClick={viewStarship(element.id)} style={{color:'white'}}> {element.name}</a>
                      </li>
                    )
                }
            )))
            if (listPeople.length !== 0) {
              setResult(Array.prototype.concat.apply([], listPeople))
            }

            if (body['data'][0] && body['data'][0].hasOwnProperty('next')) {
              starshipPageVal = starshipPageVal + 1
              starshipsFetch()
            }
          }
      });
    }

    // main html is rendered below
    return (
        <div className="App">
          <div className="topnav">
            <a className="non-clickable" href="#" onClick={homePageFetch}>The Star Wars API</a>
            <a className="links people" href="#people" onClick={peopleFetch}>People</a>
            <a className="links planets" href="#planets" onClick={planetFetch}>Planets</a>
            <a className="links starships" href="#starships" onClick={starshipsFetch}>Starships</a>
          </div>
          <br></br>
          <div className="homepage">
            <div className="star-header first-header">Base API: 
              <a className="link-style" href="https://swapi.dev/documentation">The Star Wars API</a>
            </div>
            <div className="star-header">Built by: 
              <a className="link-style" href="https://github.com/athiraindrakumar396">Athira Indrakumar</a>
            </div>
            <div className="star-header">Code available in: 
              <a className="link-style" href="https://github.com/athiraindrakumar396">Github</a>
            </div>
          </div>
          <div className='gallery'>
            <input className="filter-text" type="text" placeholder="Filter People..." onKeyUp={listenToKey} />
            <ul className="columns" data-columns="4">
              { result }
            </ul>
          </div>
          <div className='gallery'>
            <input className="filter-text" type="text" placeholder="Filter People..." onKeyUp={listenToKey} />
            <ul className="columns" data-columns="4">
              { result }
            </ul>
          </div>
        </div>
    );
}

export default App;
