import { useState, useEffect } from "react";
import $ from "jquery";
import "./App.css";

function App() {
    const [name, setName] = useState("");
    const [result, setResult] = useState("");
    const [post, getPost] = useState([])
    const API = 'http://localhost:8000/api/list-people.php';
    const fetchPost = () => {
      fetch(API)
        .then((res) => res.json())
        .then((res) => {
          console.log(res)
          getPost(res)
        })
    }
    useEffect(() => {
      fetchPost()
    }, [])

    return (
        <div className="App">
          <div className="topnav">
            <a className="non-clickable" href="#">The Star Wars API</a>
            <a className="links people" href="http://localhost:8000/api/list-people.php">People</a>
            <a className="links planets" href="http://localhost:8000/api/list-people.php">Planets</a>
            <a className="links starships" href="http://localhost:8000/api/list-people.php">Starships</a>
          </div>
          <br></br>
          <div className="star-header first-header">Base API: 
            <a className="link-style" href="https://swapi.dev/documentation">The Star Wars API</a>
          </div>
          <div className="star-header">Built by: 
            <a className="link-style" href="https://github.com/athiraindrakumar396">Athira Indrakumar</a>
          </div>
          <div className="star-header">Code available in: 
            <a className="link-style" href="https://github.com/athiraindrakumar396">Github</a>
          </div>
            <h1>{post.map((item, i) => {
          return <li key={i}>{item.title}</li>
        })}</h1>
        </div>
    );
}

export default App;
