import { useState } from "react";
import $ from "jquery";
import "./App.css";

function App() {
    const [name, setName] = useState("");
    const [result, setResult] = useState("");

    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSumbit = (e) => {
        e.preventDefault();
        const form = $(e.target);
        $.ajax({
            type: "POST",
            url: form.attr("action"),
            data: form.serialize(),
            success(data) {
                setResult(data);
            },
        });
    };

    return (
        <div className="App">
          <div className="topnav">
            <div className="non-clickable">The Star Wars API</div>
            <a className="active" href="#people">People</a>
            <a href="#planets">Planets</a>
            <a href="#starships">Starships</a>
          </div>
          <div className="star-header">
            <span>
              <a className="link-style" href="https://swapi.dev/documentation">Data Provided by: The Star Wars API</a>
            </span>
          </div>
            <h1>{result}</h1>
        </div>
    );
}

export default App;
