import { useState } from "react";
import $ from "jquery";
import "./App.css";

function App() {
    const [name, setName] = useState("");
    const [result, setResult] = useState("");

    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSumbit = (e) => {
        e.preventDefault();
        const form = $(e.target);
        $.ajax({
            type: "POST",
            url: form.attr("action"),
            data: form.serialize(),
            success(data) {
                setResult(data);
            },
        });
    };

    return (
        <div className="App">
            <div className="star-header">
              <span>
                <a href="https://swapi.dev/documentation">Data Provided by: The Star Wars API</a>
              </span>
            </div>
            <div>
              <a href="?cat=people">People&nbsp;&nbsp;*&nbsp;&nbsp;</a>
              <a href="?cat=species">Species&nbsp;&nbsp;*&nbsp;&nbsp;</a>
              <a href="?cat=planets">Planets&nbsp;&nbsp;*&nbsp;&nbsp;</a>
              <a href="?cat=starships">Starships&nbsp;&nbsp;*&nbsp;&nbsp;</a>
              <a href="?cat=vehicles">Vehicles&nbsp;&nbsp;*&nbsp;&nbsp;</a>
              <a href="?cat=films">Films&nbsp;&nbsp;*&nbsp;&nbsp;</a>
            </div>
            <h1>{result}</h1>
        </div>
    );
}

export default App;
