import { useState } from "react";
import React from 'react';
import $ from "jquery";
import "./App.css";
import axios from 'axios';

function App() {
    let pageVal = 1;
    let listPeople = [];
    const [name, setName] = useState("");
    const [page, setPage] = useState("");
    const [result, setResult] = useState("");

    const HomeAPI = 'http://localhost:8000/api/index.php';
    const PeopleAPI = 'http://localhost:8000/api/list-people.php';
    const PlanetsAPI = 'http://localhost:8000/api/list-planets.php';
    const StarshipsAPI = 'http://localhost:8000/api/list-starships.php';
    const PersonAPI = 'http://localhost:8000/api/search-person.php';

    const hideHomePage = () => {
      $('.homepage').hide()
    }

    const showHomePage = () => {
      $('.homepage').show()
    }

    const showPeople = () => {
      $('.gallery').show()
    }

    const hidePeople = () => {
      $('.gallery').hide()
    }

    const hideFilterBox = () => {
      $('.filter-text').hide()
    }

    const showFilterBox = () => {
      $('.filter-text').show()
    }

    const homePageFetch = () => {
      fetch(HomeAPI)
      .then(response => response.text())
      .then(text => {
          showHomePage()
          hidePeople();
          hideFilterBox()
      });
    }

    const peopleFetch = () => {
      axios
        .post(PeopleAPI, {
            pageValue: pageVal
        })
      .then(function(body) {
          hideHomePage()
          showPeople()
          showFilterBox()
          console.log(body['data'])
          const listItems = body['data'].map(
              (element, i) => {
                  return (
                    <li className={'listPeople'}  style={{marginTop: '30px', listStyleType: 'none'}} key={i}>
                       <a href="#view" style={{color:'white'}}> {element.name}</a>
                    </li>
                  )
              }
          )
          listPeople.push(listItems)
          console.log(listPeople)
          setResult(listPeople)

          if (body['data'][0] && body['data'][0].hasOwnProperty('next')) {
            pageVal = pageVal + 1
            peopleFetch()
          }
      });
    }

    const planetFetch = () => {
      fetch(PlanetsAPI)
      .then(response => response.text())
      .then(text => {
          hideHomePage()
          hideFilterBox()
          setResult(text)
      });
    }

    const starshipsFetch = () => {
      fetch(StarshipsAPI)
      .then(response => response.text())
      .then(text => {
          hideHomePage()
          hideFilterBox()
          setResult(text)
      });
    }

    const listenToKey = (e) => {
        axios
        .post(PersonAPI, {
            item: e.target.value
        })
        .then(function(body) {
          console.log(body['data'])
          const listItems = body['data'].map(
              (element, i) => {
                  return (
                    <li className={'listPeople'}  style={{marginTop: '30px', listStyleType: 'none'}} key={i}>
                       <a href="#view" style={{color:'white'}}> {element.name}</a>
                    </li>
                  )
              }
          )

          setResult(listItems)
        })
        .catch(function(error) {
          console.log(error);
        });
    }

    const loadMore = () => {
      axios
        .post(PeopleAPI, {
            pageValue: page
        })
      .then(function(body) {
          console.log(body['data'])
          const listItems = body['data'].map(
              (element, i) => {
                  return (
                    <li className={'listPeople'}  style={{marginTop: '30px', listStyleType: 'none'}} key={i}>
                       <a href="#view" style={{color:'white'}}> {element.name}</a>
                    </li>
                  )
              }
          )
          result.push(listItems)

          if (body['data'][0] && body['data'][0].hasOwnProperty('next')) {
            $('.load-more').show()
            setPage(page + 1)
          } else {
            $('.load-more').hide()
          }
      });
    }

    return (
        <div className="App">
          <div className="topnav">
            <a className="non-clickable" href="#" onClick={homePageFetch}>The Star Wars API</a>
            <a className="links people" href="#people" onClick={peopleFetch}>People</a>
            <a className="links planets" href="#planets" onClick={planetFetch}>Planets</a>
            <a className="links starships" href="#starships" onClick={starshipsFetch}>Starships</a>
          </div>
          <br></br>
          <div className="homepage">
            <div className="star-header first-header">Base API: 
              <a className="link-style" href="https://swapi.dev/documentation">The Star Wars API</a>
            </div>
            <div className="star-header">Built by: 
              <a className="link-style" href="https://github.com/athiraindrakumar396">Athira Indrakumar</a>
            </div>
            <div className="star-header">Code available in: 
              <a className="link-style" href="https://github.com/athiraindrakumar396">Github</a>
            </div>
          </div>
          <div className='gallery'>
            <input className="filter-text" type="text" placeholder="Filter People..." onKeyUp={listenToKey} />
            <ul className="columns" data-columns="2">
              { result }
            </ul>
             <a onClick={loadMore} className="load-more">Load More</a>
          </div>
        </div>
    );
}

export default App;
