import { useState } from "react";
import $ from "jquery";
import "./App.css";

function App() {
    const [name, setName] = useState("");
    const [result, setResult] = useState("");

    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSumbit = (e) => {
        e.preventDefault();
        const form = $(e.target);
        $.ajax({
            type: "POST",
            url: form.attr("action"),
            data: form.serialize(),
            success(data) {
                setResult(data);
            },
        });
    };

    return (
        <div className="App">
            <form
                action="http://localhost:8000/server.php"
                method="post"
                onSubmit={(event) => handleSumbit(event)}
            >
                <div style="text-align:center;width:80%;padding-top:20px;background:black;margin-left:auto;margin-right:auto;"> <img src="/img/swBanner.png" style="max-width:100%;"/><br/>
<span style="font-family:Tahoma;font-size:10pt;"><a href="https://swapi.co/">Data Provided by: The Star Wars API</a></span>
</div>
<div style="text-align:center;width:80%;background:black;margin-left:auto;margin-right:auto;">
<a href="?cat=people">People&nbsp;&nbsp;*&nbsp;&nbsp;</a>
<a href="?cat=species">Species&nbsp;&nbsp;*&nbsp;&nbsp;</a>
<a href="?cat=planets">Planets&nbsp;&nbsp;*&nbsp;&nbsp;</a>
<a href="?cat=starships">Starships&nbsp;&nbsp;*&nbsp;&nbsp;</a>
<a href="?cat=vehicles">Vehicles&nbsp;&nbsp;*&nbsp;&nbsp;</a>
<a href="?cat=films">Films&nbsp;&nbsp;*&nbsp;&nbsp;</a>
</div>
                <br />
                <button type="submit">Submit</button>
            </form>
            <h1>{result}</h1>
        </div>
    );
}

export default App;
